import React, { useState } from "react";

import "./ExpenseFilter.css";

const ExpensesFilter = () => {
    const[defaultYear,setYear]=useState('0000')
    const selectedYearHandler =(event)=>{
        setYear(event.target.value)
    }
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={selectedYearHandler}>
          <option value="2022">{defaultYear}</option>
          <option value="2021">{defaultYear}</option>
          <option value="2020">{defaultYear}</option>
          <option value="2019">{defaultYear}</option>
        </select>
      </div>
    </div>
  );
};
export default ExpensesFilter;
