import './ExpenseForm.css'
const ExpenseForm=()=>{
    
    const changeTitleHandler=(event)=>{
        console.log(event.target.value)
    }
    const changeAmountHandler=(event)=>{
        console.log(event.target.value)
    }
    const changeDateHandler=(event)=>{
        console.log(event.target.value)
    }
    return (<form >
        <div className="new-expense__controls">
        <div className="new-expense__control">
            <label >Name</label>
            <input type="text" onChange={changeTitleHandler}/>
        </div>
        <div className="new-expense__control" onChange={changeAmountHandler}>
            <label >Amount</label>
            <input type="number"  min="0.01" step="o.01"/>
        </div>
        <div className="new-expense__control" onChange={changeDateHandler}>
            <label >Date</label>
            <input type="date"  min="2019-01-01" max="2022-12-31"/>
        </div>
        </div>
        <div className='new-expense__actions'>
            <button type="submit">Add Expense</button>
        </div>

    
    </form>
    );
}

export default ExpenseForm;
