import React from "react";
import ReactDOM from "react-dom";
import Card from "./Card";
import Button from "./Button";
import classes from "./ErrorModal.module.css";
const Backdrop=(props)=>{
  return <div className={props.className} onClick={props.onConfirm}/>
}
const Overlay=(props)=>{
<Card className={classes.modal}>
        <header className={classes.header}>
          <h2>{props.title}</h2>
        </header>
        <div className={classes.content}>
          <p>{props.message}</p>
        </div>
        <footer className={classes.actions}>
          <Button onClick={props.onConfirm}>Okay</Button>
        </footer>
      </Card>
}
const ErrorModal = (props) => {
    
  return (
    <React.Fragment>
      {ReactDOM.createPortal(<Backdrop className={classes.backdrop} onConfirm={props.onConfirm}></Backdrop>
      ,document.getElementById("backdrop"))}
       {ReactDOM.createPortal(<Overlay title={props.title} message={props.message} onConfirm={props.onConfirm}></Overlay>
      ,document.getElementById("errormodal"))}

      
      
    </React.Fragment>
  );
};
export default ErrorModal;
