import React,{useState} from "react";
import FormComponent from "./Component/FormComponent";
import CreateComponent from "./Component/CreateComponent";

const DummyUser=[{
  name:"sachin",
  age:20
}]
function App() {
  const[addedUser,setAddedUser]=useState(DummyUser)


 const addUserHandler=(user)=>{
    setAddedUser((prevState)=>[{
      ...prevState,
      user
    }])

 }

  return (
    <div>
      <div>
      <FormComponent  onAdd={addUserHandler}></FormComponent>
      </div>
      <div>
        <CreateComponent user={addedUser}  ></CreateComponent>
      </div>
    </div>
  );
}

export default App;
